import React, { Component } from "react";
import { Link } from 'react-router-dom';
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";
import {CallToAction, Hero} from "react-landing-page";
import "./App.css";

const useStyles = makeStyles(theme => ({
    table: {
        minWidth: 600
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    paper: {
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        margin: `10px`,
        height: "100%",
        width: "99%",
        marginTop: theme.spacing(7)
    },
    link: {
        color: "rgba(0,0,0,0.65)",
        textDecoration: "none",
        marginLeft: "10%",
        alignSelf: "flex-start",
        "&:hover": {
            color: "rgba(0,0,0,1)"
        }
    }
}));

export default function SearchResults(props) {
    const classes = useStyles();
    const [data, upDateData] = React.useState([]);
    const [firstLoad, setLoad] = React.useState(true);
    const searchVal = props.location.search.substr(1);
    let isLoading = true;
    let empId;
    let found = false;

    async function load() {
        let response = await fetch("/api/employee");
        let body = await response.json();
        upDateData(body);
    }

    async function deleteEmp() {
        let resp = fetch("/api/employee/" + empId, {
            method: 'delete'
        })
            .then(response => response.json());
        if(resp !== null) {
            this.forceUpdate();
        }
        return resp;

    /*    let response = await fetch("/api/employee/" + empId, {
            method: "DELETE", // *GET, POST, PUT, DELETE, etc.
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json"
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer", // no-referrer, *client
        }); */
    }

    if (firstLoad) {
        load();
        setLoad(false);
    }

    if (data.length > 0) isLoading = false;

    return (
        <Hero
            color="black"
            bg="white"
            backgroundImage="data:image/jpeg;base64,"
        >
            <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                    <GroupIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                    Employee Directory
                </Typography>

                {isLoading ? (
                    <CircularProgress />
                ) : (
                    <TableContainer
                        style={{ width: "80%", margin: "0 10px" }}
                        component={Paper}
                    >
                        <Table className={classes.table} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell align="center">Id</TableCell>
                                    <TableCell align="center">Name</TableCell>
                                    <TableCell align="center">Department</TableCell>
                                    <TableCell align="center">Gender</TableCell>
                                    <TableCell align="center">Dob</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {data?.map(row => {
                                    if (row.name === searchVal) {
                                        empId = row.id;
                                        found = true;
                                        return (
                                        <TableRow key={row.name}>
                                            <TableCell align="center">{row.id}</TableCell>
                                            <TableCell align="center">{row.name}</TableCell>
                                            <TableCell align="center">{row.department}</TableCell>
                                            <TableCell align="center">{row.gender}</TableCell>
                                            <TableCell align="center">{row.dob}</TableCell>
                                        </TableRow>
                                        )}})}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}
                <Link className={classes.link} to="/search">
                    {" "}
                    <Typography align="left">
                        &#x2190; New search
                    </Typography>{" "}
                </Link>
                {found ? (
                    <CallToAction onClick={deleteEmp} mt={3}>Delete employee</CallToAction>
                ) : <div>No results found. Please try a new search</div>
                }
            </div>
        </Hero>
    );
}
