import React, { Component } from "react";
import {Hero} from "react-landing-page";
import Container from "@material-ui/core/Container/Container";
import CssBaseline from "@material-ui/core/CssBaseline/CssBaseline";
import Grid from "@material-ui/core/Grid/Grid";
import TextField from "@material-ui/core/TextField/TextField";
import Button from "@material-ui/core/Button/Button";
import {Link} from "react-router-dom";
import "./App.css"

class UpdateEmployee extends Component {
    constructor() {
        super();
        this.state = {
            id: "",
            name: "",
            department: "",
            gender: "",
            dob: ""
        }
        this.deleteEmp = this.deleteEmp.bind(this);
    }

    componentDidMount() {
        this.setState({
            id: this.props.location.state.id,
            name: this.props.location.state.name,
            department: this.props.location.state.department,
            gender: this.props.location.state.gender
        })
    }

    handleNameChange = (event) => {
        this.setState({
            name: event.target.value
        })
    }

    handleDepartmentChange = (event) => {
        this.setState({
            department: event.target.value
        })
    }

    handleGenderChange = (event) => {
        this.setState({
            gender: event.target.value
        })
    }

    deleteEmp() {
        let resp = fetch("/api/employee/" + this.state.id, {
            method: 'delete'
        })
            .then(response => response.json());
        return resp;
    }

    updateEmployee = () => {
        fetch("/api/employee" , {
            method: "PUT", // *GET, POST, PUT, DELETE, etc.
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json"
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer", // no-referrer, *client
            body: JSON.stringify(this.state)
        })
            .then((res) => res.json())
            .then((data) => console.log(data.department));
    }

    render() {
        return (
            <Hero
                color="black"
                bg="white"
                backgroundImage="data:image/jpeg;base64,"
            >
                <div className="header">
                    <h1>Please Edit Employee Information Below</h1>
                </div>
                <Container component="main" maxWidth="xs">
                    <CssBaseline />
                    <div className="paper">
                        <form className="form" noValidate>
                            <Grid container spacing={2}>
                                <Grid item xs={12}>
                                    <TextField
                                        variant="outlined"
                                        required
                                        fullWidth
                                        id="name"
                                        value={this.state.name}
                                        label="Name"
                                        name="name"
                                        autoComplete="name"
                                        onChange={this.handleNameChange}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        autoComplete="department"
                                        name="department"
                                        variant="outlined"
                                        required
                                        fullWidth
                                        value={this.state.department}
                                        id="department"
                                        label="Department"
                                        onChange={this.handleDepartmentChange}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        variant="outlined"
                                        required
                                        fullWidth
                                        id="gender"
                                        value={this.state.gender}
                                        label="Gender"
                                        name="gender"
                                        autoComplete="gender"
                                        onChange={this.handleGenderChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        id="date"
                                        label="Date of birth"
                                        type="date"
                                        className='textField'
                                        defaultValue="1998-04-02"
                                        InputLabelProps={{
                                            shrink: true
                                        }}
                                    />
                                </Grid>
                            </Grid>
                            <Grid container justify="center">
                                <Grid item>
                                    <Link to="/view">View Employee Records</Link>
                                </Grid>
                            </Grid>
                        </form>
                    </div>
                </Container>
                <div className="mod-buttons">
                    <div className="delete-button">
                        <Button halfWidth
                                variant="contained"
                                color="secondary"
                                preventDefault
                                onClick={this.deleteEmp}
                                component={Link}
                                to="/delete">
                            Delete employee
                        </Button>
                    </div>
                    <div>
                        <Button component={Link}
                                halfWidth
                                variant="contained"
                                color="primary"
                                onClick={this.updateEmployee}
                                preventDefault
                                to="/delete"
                        >Update information
                        </Button>
                    </div>
                </div>
            </Hero>
        )
    }
}

export default UpdateEmployee;
