import React, { Component } from "react";
import {Hero} from "react-landing-page";
import Container from "@material-ui/core/Container/Container";
import CssBaseline from "@material-ui/core/CssBaseline/CssBaseline";
import Avatar from "@material-ui/core/Avatar/Avatar";
import GroupIcon from "@material-ui/core/SvgIcon/SvgIcon";
import Typography from "@material-ui/core/Typography/Typography";
import Grid from "@material-ui/core/Grid/Grid";
import TextField from "@material-ui/core/TextField/TextField";
import Button from "@material-ui/core/Button/Button";
import {Link} from "react-router-dom";
import {makeStyles} from "@material-ui/core";

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

class UpdateEmployee extends Component {
    constructor() {
        super();
        const classes = useStyles();
        this.state = {
            name: "",
            department: "",
            gender: "",
            dob: ""
        }
    }

    componentDidMount() {
        this.setState({
            name: this.props.location.state.name,
            department: this.props.location.state.department,
            gender: this.props.location.state.gender
        })
    }

    handleNameChange = (event) => {
        this.setState({
            name: event.target.value
        })
        console.log(this.state.name);
    }

    handleDepartmentChange = (event) => {
        this.setState({
            department: event.target.value
        })
    }

    handleGenderChange = (event) => {
        this.setState({
            gender: event.target.value
        })
    }

    handleSubmit = () => {

    }

    render() {
        return (
            <Hero
                color="black"
                bg="white"
                backgroundImage="data:image/jpeg;base64,"
            >
                <Container component="main" maxWidth="xs">
                    <CssBaseline />
                    <div>
                        <Avatar>
                            <GroupIcon />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Employee Directory
                        </Typography>
                        <form noValidate>
                            <Grid container spacing={2}>
                                <Grid item xs={12}>
                                    <TextField
                                        variant="outlined"
                                        required
                                        fullWidth
                                        id="name"
                                        value={this.state.name}
                                        label="Name"
                                        name="name"
                                        autoComplete="name"
                                        onChange={this.handleNameChange}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        autoComplete="department"
                                        name="department"
                                        variant="outlined"
                                        required
                                        fullWidth
                                        value={this.state.department}
                                        id="department"
                                        label="Department"
                                        onChange={this.handleDepartmentChange}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        variant="outlined"
                                        required
                                        fullWidth
                                        id="gender"
                                        value={this.state.gender}
                                        label="Gender"
                                        name="gender"
                                        autoComplete="gender"
                                        onChange={this.handleGenderChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        id="date"
                                        label="Date of birth"
                                        type="date"
                                        defaultValue="1998-04-02"
                                        InputLabelProps={{
                                            shrink: true
                                        }}
                                    />
                                </Grid>
                            </Grid>
                            <Button
                                // type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                preventDefault
                                onClick={this.handleSubmit}
                            >
                                Save
                            </Button>

                            <Grid container justify="center">
                                <Grid item>
                                    <Link to="/view">View Employee Records</Link>
                                </Grid>
                            </Grid>
                        </form>
                    </div>
                </Container>
            </Hero>
        )
    }
}

export default UpdateEmployee;
